func track(msg string) (string, time.Time) {
	return msg, time.Now()
}

func duration(msg string, start time.Time) {
	fmt.Printf("%v: %v\n", msg, time.Since(start))
}

func quicksort(a []int) []int {
	// We're done when there's a single item left
	if len(a) < 2 {
		return a
	}

	left, right := 0, len(a)-1
	pivot := rand.Int() % len(a)

	// Swap right bound with random pivot point
	a[pivot], a[right] = a[right], a[pivot]

	for i := range a {
		// If current is less than the originally chosen pivot point,
		// swap it with the left bound (move it to the front) then move left bound up
		if a[i] < a[right] {
			a[left], a[i] = a[i], a[left]
			left++
		}
	}

	// Swap original pivot value with the left bound
	// We ran out of numbers smaller than pivot,
	// so all numbers on left of this point will be smaller
	// and all numbers on right will be larger
	a[left], a[right] = a[right], a[left]

	// Divide and conquer: Sort items to the left and to the right
	quicksort(a[:left])
	quicksort(a[left+1:])

	return a
}
